package day0205;

import java.util.Scanner;

public class 스도쿠검증 {

	public static void main(String[] args) {
		Scanner scan = new Scanner(System.in);
		int testcase = scan.nextInt();
		for(int t=1; t<=10; ++t)
		{
			int ans = 0;
			int [][] arr = new int[9][9];
			for(int i=0; i<9; ++i)
				for(int j=0; j<9; ++j)
					arr[i][j] = scan.nextInt();
			boolean flag = true;
			//가로검사
			int[] count = new int[9];
			
			if(flag)
			{
				for(int i=0; i<9; ++i)
				{
					for(int j=0; j<9; ++j)
					{
						++count[arr[i][j]];
					}
					for(int j=0; j<9; ++j)
					{
						if(count[j] != 1)
						{
							flag = false;
							break;
						}
					}
					for(int j=0; j<9; ++j)
						count[j] = 0;
				}	
			}
			
			for(int j=0; j<9; ++j)
				count[j] = 0;
			
			//세로검사
			if(flag)
			{
				for(int i=0; i<9; ++i)
				{
					for(int j=0; j<9; ++j)
					{
						++count[arr[j][i]];
					}
					for(int j=0; j<9; ++j)
					{
						if(count[j] != 1)
						{
							flag = false;
							break;
						}
					}
					for(int j=0; j<9; ++j)
						count[j] = 0;
				}	
			}
			
			for(int j=0; j<9; ++j)
				count[j] = 0;
			
			//3by3 9번검사
			if(flag)
			{
				for(int i=0; i<9; i+=3)
				{
					for(int j=i; j<i+3; ++j)
					{
						
							for(int l=k; l<k+3; ++l)
							{
								++count[arr[j][l]];
							}
						
					}
					
				}
			}
			
			System.out.println("#"+t+" "+ (flag ? "1" : "0"));
		}

	}

}
